// Daily Reset Check
const now = new Date();
const hours = now.getHours();
const minutes = now.getMinutes();

// Execute reset between 00:00 and 00:30 to avoid multiple executions
if (hours === 0 && minutes <= 30) {

  // Get all active email accounts from Airtable
  const activeAccounts = $('Get All Email Accounts').all()
    .filter(item => item.json['Status'] === 'Active')
    .map(item => ({
      id: item.json.id,
      email: item.json['Email Address'],
      currentSent: item.json['Sent Today'] || 0
    }));

  return [{
    json: {
      shouldReset: true,
      accountCount: activeAccounts.length,
      resetDate: now.toISOString().split('T')[0],
      accounts: activeAccounts
    }
  }];
}

return [{ json: { shouldReset: false, message: "Outside reset window" } }];

// Aggregate email addresses and SparkPost data
const allAddresses = $('Get All Email Accounts').all();
const sparkpostData = $node["Get SparkPost IP Status"].json;
const postalRoutes = $node["Get Postal Routes Status"].json;

// Group by domain instead of campaign
const domainGroups = {};
for (const item of allAddresses) {
  const email = item.json['Email Address'];
  if (!email) continue;

  const domain = email.split('@')[1];
  if (!domainGroups[domain]) {
    domainGroups[domain] = [];
  }
  domainGroups[domain].push(item.json);
}

// Extract SparkPost IP limits
let currentStage = 1;
let stageLimit = 50; // Ultra-conservative start

const stageLimits = {
  1: 200, 2: 500, 3: 1000, 4: 2000, 5: 5000,
  6: 10000, 7: 20000, 8: 40000, 9: 50000, 10: 75000,
  11: 100000, 12: 150000, 13: 200000, 14: 250000, 15: 325000,
  16: 400000, 17: 500000, 18: 600000, 19: 750000, 20: 1000000
};

const ips = sparkpostData.results || [];
for (const ip of ips) {
  if (ip.ip_pool === 'YOUR_IP_POOL_NAME' && ip.auto_warmup_enabled && ip.auto_warmup_stage) {
    currentStage = ip.auto_warmup_stage;
    stageLimit = stageLimits[currentStage] || stageLimit;
    break;
  }
}

// Apply 15% safety buffer
const effectiveLimit = Math.floor(stageLimit * 0.85);

return [{
  json: {
    sparkpost: {
      currentStage,
      stageLimit,
      effectiveLimit,
      ipPool: 'YOUR_IP_POOL_NAME'
    },
    domains: domainGroups,
    postalRoutes: postalRoutes || [],
    timestamp: new Date().toISOString()
  }
}];

// Extract unique sending domains from email addresses
const addressesData = $('Get All Email Accounts').all();
const domains = new Set();

for (const item of addressesData) {
  const email = item.json['Email Address'];
  if (email && email.includes('@')) {
    const domain = email.split('@')[1];
    domains.add(domain);
  }
}

const domainList = Array.from(domains);

// Calculate date range for metrics (last 24 hours)
const now = new Date();
const yesterday = new Date(now.getTime() - 24 * 60 * 60 * 1000);

const fromDate = yesterday.toISOString().split('.')[0];
const toDate = now.toISOString().split('.')[0];

return [{
  json: {
    domains: domainList,
    domainString: domainList.join(','),
    fromDate,
    toDate,
    metricsToFetch: 'count_targeted,count_injected,count_bounce,count_hard_bounce,count_soft_bounce,count_spam_complaint,count_unsubscribe,count_delivered'
  }
}];

// Enhanced email address processing with domain metrics
const addressesData = $('Get All Email Accounts').all();
const aggregatedData = $node["Aggregate Domain & SparkPost Data"].json;
const sparkpostMetrics = $node["Get SparkPost Domain Metrics"].json.results || [];
const today = new Date();
const results = [];
const healthIssues = [];
const campaignVolumes = {};
const domainMetrics = {};

// Process SparkPost domain metrics
for (const metric of sparkpostMetrics) {
  const domain = metric.sending_domain;
  const targeted = metric.count_targeted || 0;
  const bounced = metric.count_bounce || 0;
  const complaints = metric.count_spam_complaint || 0;

  domainMetrics[domain] = {
    bounceRate: targeted > 0 ? (bounced / targeted * 100) : 0,
    complaintRate: targeted > 0 ? (complaints / targeted * 100) : 0,
    targeted,
    bounced,
    complaints,
    unsubscribed: metric.count_unsubscribe || 0
  };
}

// Initialize campaign volume tracking
for (const campaignId in aggregatedData.campaigns) {
  campaignVolumes[campaignId] = {
    current: 0,
    projected: 0,
    mailboxCount: 0
  };
}

// Conservative warmup schedule (6 weeks)
function getMaxIncrement(weeksActive) {
  if (weeksActive <= 2) return 2;      // Weeks 1-2: Max +2/day
  if (weeksActive <= 4) return 3;      // Weeks 3-4: Max +3/day
  if (weeksActive <= 6) return 4;      // Weeks 5-6: Max +4/day
  return 5;                             // After 6 weeks: Max +5/day
}

function getTargetLimit(daysActive) {
  const weeksActive = Math.floor(daysActive / 7);

  if (weeksActive < 1) return 2;       // Week 1: 1-2 emails
  if (weeksActive < 2) return 4;       // Week 2: 3-4 emails
  if (weeksActive < 3) return 10;      // Week 3: 8-10 emails
  if (weeksActive < 4) return 18;      // Week 4: 15-18 emails
  if (weeksActive < 5) return 28;      // Week 5: 25-28 emails
  if (weeksActive < 6) return 38;      // Week 6: 35-38 emails
  return 40;                            // Target: 40 emails
}

// Process each mailbox
for (const item of mailboxesData) {
  const mailbox = item.json;
  const email = mailbox.email;
  const domain = email.split('@')[1];
  const campaignId = mailbox.campaign_id;
  let currentLimit = parseInt(mailbox.current_limit) || 0;
  let warmupStartDate = mailbox.warmup_start_date;
  let lastUpdated = mailbox.last_updated;
  let healthStatus = mailbox.health_status || 'healthy';
  let daysActive = 0;
  let isNew = false;
  let isPaused = false;
  let issues = [];

  // Get domain metrics from SparkPost
  const domainData = domainMetrics[domain] || { bounceRate: 0, complaintRate: 0 };
  const bounceRate = domainData.bounceRate;
  const complaintRate = domainData.complaintRate;

  // Validate campaign assignment
  if (!campaignId || !aggregatedData.campaigns[campaignId]) {
    issues.push('No valid campaign assigned');
    healthStatus = 'error';
  }

  // Initialize new mailboxes
  if (!warmupStartDate || currentLimit === 0) {
    warmupStartDate = today.toISOString().split('T')[0];
    currentLimit = 0;
    daysActive = 0;
    isNew = true;
  } else {
    const startDate = new Date(warmupStartDate);
    daysActive = Math.floor((today - startDate) / (1000 * 60 * 60 * 24));
  }

  // Health checks with SparkPost data - ADJUSTED FOR WARMUP
  if (bounceRate > 10) {  // Changed from 5% to 10%
    isPaused = true;
    issues.push(`High bounce rate: ${bounceRate.toFixed(1)}% (Domain: ${domain})`);
    healthStatus = 'paused';
  }

  if (complaintRate > 0.3) {  // Changed from 0.1% to 0.3%
    isPaused = true;
    issues.push(`High complaint rate: ${complaintRate.toFixed(2)}% (Domain: ${domain})`);
    healthStatus = 'paused';
  }

  // Check for stuck mailboxes
  if (lastUpdated) {
    const lastUpdateDate = new Date(lastUpdated);
    const daysSinceUpdate = Math.floor((today - lastUpdateDate) / (1000 * 60 * 60 * 24));
    if (daysSinceUpdate > 3 && currentLimit < 40) {
      issues.push(`No progress for ${daysSinceUpdate} days`);
      healthStatus = 'stuck';
    }
  }

  // Track current volume per campaign
  if (campaignId && aggregatedData.campaigns[campaignId]) {
    campaignVolumes[campaignId].current += currentLimit;
    campaignVolumes[campaignId].mailboxCount++;
  }

  results.push({
    email,
    domain,
    campaignId,
    warmupStartDate,
    currentLimit,
    daysActive,
    weeksActive: Math.floor(daysActive / 7),
    bounceRate,
    complaintRate,
    domainMetrics: domainData,
    isNew,
    isPaused,
    healthStatus,
    issues,
    targetLimit: getTargetLimit(daysActive),
    maxIncrement: getMaxIncrement(Math.floor(daysActive / 7))
  });

  if (issues.length > 0) {
    healthIssues.push({ email, domain, issues });
  }
}

// Calculate available room per campaign
const totalCurrentVolume = results.reduce((sum, mb) => sum + mb.currentLimit, 0);
const availableRoom = Math.max(0, aggregatedData.sparkpost.effectiveLimit - totalCurrentVolume);

// Sort mailboxes by priority within each campaign
const sortedByCampaign = {};
for (const campaignId in aggregatedData.campaigns) {
  sortedByCampaign[campaignId] = results
    .filter(mb => mb.campaignId === campaignId)
    .sort((a, b) => {
      if (a.isPaused && !b.isPaused) return 1;
      if (!a.isPaused && b.isPaused) return -1;
      if (a.healthStatus === 'stuck' && b.healthStatus !== 'stuck') return -1;
      if (a.healthStatus !== 'stuck' && b.healthStatus === 'stuck') return 1;
      if (a.isNew && !b.isNew) return -1;
      if (!a.isNew && b.isNew) return 1;
      return a.currentLimit - b.currentLimit;
    });
}

// Distribute available room across campaigns
let remainingRoom = availableRoom;
const processedMailboxes = [];

for (const campaignId in sortedByCampaign) {
  const campaignMailboxes = sortedByCampaign[campaignId];
  const campaignShare = Math.floor(availableRoom / Object.keys(aggregatedData.campaigns).length);
  let campaignRoom = Math.min(campaignShare, remainingRoom);

  for (const mailbox of campaignMailboxes) {
    let increment = 0;
    let newLimit = mailbox.currentLimit;
    let reason = '';

    if (mailbox.isPaused) {
      increment = 0;
      reason = `PAUSED: ${mailbox.issues.join(', ')}`;
    } else if (mailbox.currentLimit >= 40) {
      increment = 0;
      reason = 'At target limit (40)';
    } else if (campaignRoom <= 0) {
      increment = 0;
      reason = 'No room in campaign allocation';
    } else if (remainingRoom <= 0) {
      increment = 0;
      reason = `SparkPost stage ${aggregatedData.sparkpost.currentStage} limit reached`;
    } else {
      // Calculate conservative increment
      const targetLimit = mailbox.targetLimit;
      const desiredIncrement = Math.min(
        mailbox.maxIncrement,
        targetLimit - mailbox.currentLimit,
        40 - mailbox.currentLimit
      );

      // New mailboxes start extra conservative
      const actualIncrement = mailbox.isNew ? 
        Math.min(1, desiredIncrement) : 
        Math.min(desiredIncrement, campaignRoom, remainingRoom);

      if (actualIncrement > 0) {
        increment = actualIncrement;
        newLimit = mailbox.currentLimit + increment;
        campaignRoom -= increment;
        remainingRoom -= increment;
        reason = `Week ${mailbox.weeksActive + 1} progression (+${increment})`;
      } else {
        reason = 'Capacity constraints';
      }
    }

    campaignVolumes[campaignId].projected += newLimit;

    processedMailboxes.push({
      ...mailbox,
      increment,
      newLimit,
      reason,
      shouldUpdate: increment > 0
    });
  }
}

// Calculate summary
const summary = {
  totalMailboxes: processedMailboxes.length,
  totalCurrentVolume,
  projectedNewVolume: processedMailboxes.reduce((sum, mb) => sum + mb.newLimit, 0),
  sparkpostStage: aggregatedData.sparkpost.currentStage,
  stageLimit: aggregatedData.sparkpost.stageLimit,
  effectiveLimit: aggregatedData.sparkpost.effectiveLimit,
  mailboxesToUpdate: processedMailboxes.filter(mb => mb.shouldUpdate).length,
  healthyMailboxes: processedMailboxes.filter(mb => mb.healthStatus === 'healthy').length,
  pausedMailboxes: processedMailboxes.filter(mb => mb.isPaused).length,
  stuckMailboxes: processedMailboxes.filter(mb => mb.healthStatus === 'stuck').length,
  campaignVolumes,
  healthIssues,
  domainMetrics,
  ipPool: aggregatedData.sparkpost.ipPool
};

return processedMailboxes.map(mb => ({ json: { ...mb, summary, aggregatedData } }));

// Group mailboxes by campaign and check campaign health
const mailboxes = $input.all();
const campaignGroups = {};
const campaignHealthChecks = {};

for (const item of mailboxes) {
  const mb = item.json;
  if (!mb.campaignId) continue;

  if (!campaignGroups[mb.campaignId]) {
    campaignGroups[mb.campaignId] = [];
    campaignHealthChecks[mb.campaignId] = {
      totalMailboxes: 0,
      pausedMailboxes: 0,
      avgBounceRate: 0,
      avgComplaintRate: 0,
      createdAt: mb.aggregatedData.campaigns[mb.campaignId].created_at
    };
  }
  campaignGroups[mb.campaignId].push(mb);

  const check = campaignHealthChecks[mb.campaignId];
  check.totalMailboxes++;
  if (mb.isPaused) check.pausedMailboxes++;
  check.avgBounceRate += mb.bounceRate;
  check.avgComplaintRate += mb.complaintRate;
}

// Calculate averages and determine if campaigns need pausing
for (const campaignId in campaignHealthChecks) {
  const check = campaignHealthChecks[campaignId];
  check.avgBounceRate = check.avgBounceRate / check.totalMailboxes;
  check.avgComplaintRate = check.avgComplaintRate / check.totalMailboxes;

  // Calculate campaign age
  const createdDate = new Date(check.createdAt);
  const today = new Date();
  const daysActive = Math.floor((today - createdDate) / (1000 * 60 * 60 * 24));
  check.daysActive = daysActive;
  check.weeksActive = Math.floor(daysActive / 7);

  // Determine if campaign should be paused
  check.shouldPause = false;
  check.pauseReasons = [];

  // More than 50% of mailboxes paused
  if (check.pausedMailboxes / check.totalMailboxes > 0.5) {
    check.shouldPause = true;
    check.pauseReasons.push(`${check.pausedMailboxes}/${check.totalMailboxes} mailboxes paused`);
  }

  // Average bounce rate too high - ADJUSTED FOR WARMUP
  if (check.avgBounceRate > 10) {  // Changed from 3% to 10%
    check.shouldPause = true;
    check.pauseReasons.push(`High avg bounce rate: ${check.avgBounceRate.toFixed(1)}%`);
  }

  // Average complaint rate too high - ADJUSTED FOR WARMUP
  if (check.avgComplaintRate > 0.1) {  // Changed from 0.05% to 0.1%
    check.shouldPause = true;
    check.pauseReasons.push(`High avg complaint rate: ${check.avgComplaintRate.toFixed(3)}%`);
  }
}

// Create items for each campaign that needs updating
const campaignUpdates = [];
for (const [campaignId, mailboxes] of Object.entries(campaignGroups)) {
  const campaignData = mailboxes[0].aggregatedData.campaigns[campaignId];
  const campaignVolume = mailboxes[0].summary.campaignVolumes[campaignId];
  const healthCheck = campaignHealthChecks[campaignId];

  const newLimit = campaignVolume.projected;
  const currentLimit = campaignData.daily_limit || 0;

  // Always include ALL campaigns for metrics logging
  campaignUpdates.push({
    json: {
      campaignId,
      campaignName: campaignData.name,
      currentLimit,
      newLimit,
      mailboxCount: campaignVolume.mailboxCount,
      shouldUpdate: Math.abs(newLimit - currentLimit) > 5 || healthCheck.shouldPause,
      healthCheck,
      currentStatus: campaignData.status
    }
  });
}

return campaignUpdates;

// Process all campaign items
const items = $input.all();
const results = [];

for (const item of items) {
  const analytics = item.json;
  // Get the corresponding campaign data
  const campaignData = $('Should Update Campaigns?').all()
    .find(i => i.json.campaignId === analytics.campaign_id)?.json || {};
  const healthCheck = campaignData.healthCheck || {};

  // Calculate reply rate using the correct field names
  const sent = analytics.emails_sent_count || 0;
  const replied = analytics.reply_count || 0;
  const replyRate = sent > 0 ? (replied / sent * 100) : 0;

  // Check if campaign is over 2 weeks old with low reply rate
  let shouldPauseForReplyRate = false;
  let additionalPauseReason = '';

  if (healthCheck.weeksActive >= 2 && replyRate < 1 && sent > 100) {
    shouldPauseForReplyRate = true;
    additionalPauseReason = `Low reply rate after ${healthCheck.weeksActive} weeks: ${replyRate.toFixed(2)}% (${replied}/${sent})`;
  }

  results.push({
    json: {
      ...campaignData,
      analytics: {
        sent,
        replied,
        replyRate
      },
      shouldPauseForReplyRate,
      additionalPauseReason,
      finalShouldPause: healthCheck.shouldPause || shouldPauseForReplyRate,
      allPauseReasons: [...(healthCheck.pauseReasons || []), ...(additionalPauseReason ? [additionalPauseReason] : [])]
    }
  });
}

return results;

// Comprehensive summary with campaign and domain breakdown
const allMailboxes = $('Process Mailboxes with Domain Metrics').all();
const firstItem = allMailboxes[0]?.json.summary || {};

// Group by status and campaign
const statusGroups = {
  updated: [],
  held: [],
  paused: [],
  stuck: [],
  atTarget: []
};

const campaignSummaries = {};
const domainSummaries = {};

for (const item of allMailboxes) {
  const mb = item.json;

  // Initialize campaign summary if needed
  if (!campaignSummaries[mb.campaignId]) {
    campaignSummaries[mb.campaignId] = {
      name: mb.aggregatedData.campaigns[mb.campaignId]?.name || 'Unknown',
      total: 0,
      updated: 0,
      healthy: 0,
      issues: 0,
      currentVolume: 0,
      projectedVolume: 0
    };
  }

  // Initialize domain summary if needed
  if (!domainSummaries[mb.domain]) {
    domainSummaries[mb.domain] = {
      mailboxCount: 0,
      bounceRate: mb.bounceRate || 0,
      complaintRate: mb.complaintRate || 0,
      issues: 0
    };
  }

  const cs = campaignSummaries[mb.campaignId];
  const ds = domainSummaries[mb.domain];

  cs.total++;
  cs.currentVolume += mb.currentLimit;
  cs.projectedVolume += mb.newLimit;
  ds.mailboxCount++;

  if (mb.shouldUpdate) {
    statusGroups.updated.push(`${mb.email}: ${mb.currentLimit} â†’ ${mb.newLimit}`);
    cs.updated++;
  } else if (mb.isPaused) {
    statusGroups.paused.push(`${mb.email}: ${mb.issues.join(', ')}`);
    cs.issues++;
    ds.issues++;
  } else if (mb.healthStatus === 'stuck') {
    statusGroups.stuck.push(`${mb.email}: ${mb.issues.join(', ')}`);
    cs.issues++;
  } else if (mb.currentLimit >= 40) {
    statusGroups.atTarget.push(mb.email);
  } else {
    statusGroups.held.push(`${mb.email}: ${mb.currentLimit} (${mb.reason})`);
  }

  if (mb.healthStatus === 'healthy') {
    cs.healthy++;
  }
}

// Format campaign summaries
let campaignBreakdown = '\n\nðŸ“Š Campaign Breakdown:';
for (const [id, summary] of Object.entries(campaignSummaries)) {
  campaignBreakdown += `\n\n${summary.name}:`;
  campaignBreakdown += `\nâ€¢ Mailboxes: ${summary.total}`;
  campaignBreakdown += `\nâ€¢ Volume: ${summary.currentVolume} â†’ ${summary.projectedVolume}/day`;
  campaignBreakdown += `\nâ€¢ Updated: ${summary.updated}, Healthy: ${summary.healthy}`;
  if (summary.issues > 0) {
    campaignBreakdown += `\nâ€¢ âš ï¸ Issues: ${summary.issues}`;
  }
}

// Format problematic domains
let domainIssues = '';
const problematicDomains = Object.entries(domainSummaries)
  .filter(([domain, data]) => data.bounceRate > 3 || data.complaintRate > 0.05 || data.issues > 0)
  .sort((a, b) => b[1].bounceRate - a[1].bounceRate);

if (problematicDomains.length > 0) {
  domainIssues = '\n\nâš ï¸ Domain Issues:';
  for (const [domain, data] of problematicDomains) {
    domainIssues += `\nâ€¢ ${domain}: ${data.mailboxCount} mailboxes`;
    if (data.bounceRate > 0) domainIssues += `, ${data.bounceRate.toFixed(1)}% bounce`;
    if (data.complaintRate > 0) domainIssues += `, ${data.complaintRate.toFixed(3)}% complaints`;
  }
}

// Check if all at target
const allAtTarget = statusGroups.atTarget.length === allMailboxes.length;

return [{
  json: {
    timestamp: new Date().toISOString(),
    sparkpostStage: firstItem.sparkpostStage,
    stageLimit: firstItem.stageLimit,
    effectiveLimit: firstItem.effectiveLimit,
    ipPool: firstItem.ipPool,
    totalMailboxes: firstItem.totalMailboxes,
    currentVolume: firstItem.totalCurrentVolume,
    projectedVolume: firstItem.projectedNewVolume,
    volumeIncrease: firstItem.projectedNewVolume - firstItem.totalCurrentVolume,
    percentOfLimit: Math.round((firstItem.projectedNewVolume / firstItem.effectiveLimit) * 100),
    statusGroups,
    campaignSummaries,
    domainSummaries,
    campaignBreakdown,
    domainIssues,
    healthIssues: firstItem.healthIssues,
    allAtTarget,
    summary: firstItem
  }
}];

