// Comprehensive error handling
const errors = [];
const items = $input.all();

for (const item of items) {
  if (item.error) {
    errors.push({
      node: item.error.node?.name || 'Unknown',
      message: item.error.message,
      timestamp: new Date().toISOString(),
      details: item.json || {}
    });
  }
}

if (errors.length > 0) {
  return [{
    json: {
      hasErrors: true,
      errorCount: errors.length,
      errors,
      message: `âš ï¸ Warmup Automation Errors\n\n${errors.length} errors detected:\n\n${errors.slice(0, 10).map(e => `â€¢ ${e.node}: ${e.message}`).join('\n')}${errors.length > 10 ? `\n\n... and ${errors.length - 10} more` : ''}`
    }
  }];
}

return [{ json: { hasErrors: false } }];
